// 获取工具ID的最大值
export const getMaxToolId = () => {
  if (toolsData.length === 0) return 0;
  
  // 尝试将所有ID转换为数字，忽略非数字ID
  const numericIds = toolsData
    .map(tool => parseInt(tool.id, 10))
    .filter(id => !isNaN(id));
  
  // 如果没有有效的数字ID，返回0
  if (numericIds.length === 0) return 0;
  
  // 返回最大ID值
  return Math.max(...numericIds);
};

// 获取热门工具列表（按点击量排序）
export const getHotTools = (limit = 5) => {
  return [...toolsData]
    .sort((a, b) => b.clicks - a.clicks)
    .slice(0, limit);
};

// 按平台过滤工具
export const getToolsByPlatform = (platformId) => {
  if (!platformId) return toolsData;
  return toolsData.filter(tool => 
    tool.platforms.includes(platformId)
  );
};

// 搜索工具
export const searchTools = (query) => {
  if (!query) return toolsData;
  
  const lowercaseQuery = query.toLowerCase();
  return toolsData.filter(tool => 
    tool.name.toLowerCase().includes(lowercaseQuery) ||
    tool.description.toLowerCase().includes(lowercaseQuery) ||
    tool.tags.some(tag => tag.toLowerCase().includes(lowercaseQuery))
  );
};

// 获取工具详情
export const getToolById = (id) => {
  const tool = toolsData.find(tool => tool.id === id);
  if (tool) {
    return {
      ...tool,
      categories: tool.categories || []
    };
  }
  return null;
};

// 获取相关工具推荐
export const getRelatedTools = (currentToolId, limit = 3) => {
  const currentTool = getToolById(currentToolId);
  if (!currentTool) return [];
  
  return toolsData
    .filter(tool => 
      tool.id !== currentToolId && (
        // 检查平台匹配
        (currentTool.platforms && tool.platforms && 
         tool.platforms.some(platform => currentTool.platforms.includes(platform))) || 
        // 检查分类匹配
        (currentTool.categories && tool.categories && 
         tool.categories.some(category => currentTool.categories.includes(category)))
      )
    )
    .slice(0, limit);
};

// 分页获取工具
export const getToolsPaginated = (page = 1, pageSize = 12) => {
  const startIndex = (page - 1) * pageSize;
  const endIndex = startIndex + pageSize;
  const data = toolsData.slice(startIndex, endIndex);
  
  return {
    data,
    pagination: {
      total: toolsData.length,
      pageSize,
      currentPage: page,
      totalPages: Math.ceil(toolsData.length / pageSize)
    }
  };
}; 

// 获取随机工具推荐
export const getRandomTools = (currentToolId, limit = 3) => {
  try {
    // 过滤掉当前工具
    const filteredTools = toolsData.filter(tool => tool.id !== currentToolId);
    
    // 如果没有足够的工具，则返回所有可用工具
    if (filteredTools.length <= limit) {
      return filteredTools;
    }
    
    // 随机打乱工具顺序
    const shuffled = [...filteredTools].sort(() => 0.5 - Math.random());
    
    // 返回指定数量的工具
    return shuffled.slice(0, limit);
  } catch (error) {
    console.error('获取随机工具失败:', error);
    return [];
  }
}; 